'use strict';
var chalk = require('chalk');
var table = require('text-table');
var logSymbols = require('log-symbols');
var stringLength = require('string-length');
var plur = require('plur');

module.exports = {
	toString: function () {
		return __filename;
	},
	reporter: function (result, config, options) {
		var total = result.length;
		var ret = '';
		var headers = [];
		var prevfile;
		var errorCount = 0;
		var warningCount = 0;

		options = options || {};

		ret += table(result.map(function (el, i) {
			var err = el.error;
			// E: Error, W: Warning, I: Info
			var isError = err.code && err.code[0] === 'E';

			var line = [
				'',
				chalk.bold('line ' + err.line),
				chalk.bold('col ' + err.character),
				isError ? chalk.red(err.reason) : chalk.red(err.reason)
			];

			if (el.file !== prevfile) {
				var path = el.file.split('/')
				headers[i] = path[path.length-1];
			}

			if (options.verbose) {
				line.push(chalk.bold('(' + err.code + ')'));
			}

			if (isError) {
				errorCount++;
			} else {
				warningCount++;
			}

			prevfile = el.file;

			return line;
		}), {
			stringLength: stringLength
		}).split('\n').map(function (el, i) {
			return headers[i] ? '\n' + chalk.green.bold(headers[i]) + '\n' + el : el;
		}).join('\n') + '\n\n';

		if (total > 0) {
			if (errorCount > 0) {
				ret += chalk.red.bold('✖ ' + errorCount + ' error' + (errorCount === 1 ? '' : 's')+'\n');
			}

			ret += chalk.yellow.bold('✖ ' + total + ' warning' + (total === 1 ? '' : 's'));
		} else {
			ret += '  ' + logSymbols.success + ' No problems';
			ret = '\n' + ret.trim();
		}

		console.log(ret + '\n');
	}
};
